import React, { useState, useEffect } from "react";
import axios from "axios";
import Header from "../components/Header";
import "./ProfileEdit.css";
import { Link, useParams } from "react-router-dom";

function ProfileEdit() {
  const { user_id } = useParams();
  const [formData, setFormData] = useState({
    nickname: "",
    email: "",
    password: "",
    passwordConfirm: "",
    sessions: [{ session_id: "", session_info: "" }],
  });

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(`/dailband/user/${user_id}/profile`);
        setFormData(response.data);
      } catch (error) {
        console.error(error);
      }
    };

    fetchData();
  }, [user_id]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSessionChange = (index, field, value) => {
    const newSessions = [...formData.sessions];
    newSessions[index][field] = value;
    setFormData({ ...formData, sessions: newSessions });
  };

  const addSession = () => {
    setFormData({
      ...formData,
      sessions: [...formData.sessions, { session_id: "", session_info: "" }],
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (formData.password !== formData.passwordConfirm) {
      alert("비밀번호가 일치하지 않습니다.");
      return;
    }
    try {
      await axios.put(`/dailband/user/${user_id}/profile`, formData);
      alert("프로필이 성공적으로 업데이트되었습니다.");
    } catch (error) {
      console.error(error);
      alert("프로필 업데이트 중 오류가 발생했습니다.");
    }
  };

  return (
    <div className="ProfileEdit">
      <Header />
      <div className="Sidebar">
        <h2>{formData.nickname}</h2>
        <Link to={`/profile/edit/${user_id}`}>프로필 수정</Link>
        <Link to="/scrap">스크랩</Link>
        <Link to="/MyPost">내가 쓴 글</Link>
        <Link to="/MyReservations">공연 예약</Link>
      </div>
      <div className="Content">
        <h2>마이페이지</h2>
        <form onSubmit={handleSubmit}>
          <div className="InputField">
            <label>닉네임</label>
            <input
              type="text"
              name="nickname"
              value={formData.nickname}
              onChange={handleChange}
            />
          </div>
          <div className="InputField">
            <label>이메일</label>
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
            />
          </div>
          <div className="InputField">
            <label>비밀번호 변경</label>
            <input
              type="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
            />
          </div>
          <div className="InputField">
            <label>비밀번호 확인</label>
            <input
              type="password"
              name="passwordConfirm"
              value={formData.passwordConfirm}
              onChange={handleChange}
            />
          </div>
          <div className="CheckboxGroup">
            {formData.sessions.map((session, index) => (
              <div key={index} className="SessionField">
                <label>세션 {index + 1}</label>
                <input
                  type="number"
                  placeholder="Session ID"
                  value={session.session_id}
                  onChange={(e) =>
                    handleSessionChange(index, "session_id", e.target.value)
                  }
                />
                <input
                  type="text"
                  placeholder="Session Info"
                  value={session.session_info}
                  onChange={(e) =>
                    handleSessionChange(index, "session_info", e.target.value)
                  }
                />
              </div>
            ))}
            <button type="button" onClick={addSession}>
              세션 추가
            </button>
          </div>
          <button type="submit" className="UpdateButton">
            수정
          </button>
        </form>
      </div>
    </div>
  );
}

export default ProfileEdit;
