import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Header from '../components/Header';
import { useNavigate, useParams } from 'react-router-dom';
import { Link } from "react-router-dom";
import './Scrap.css';

function Scrap() {
  const { user_id } = useParams();
  const [scraps, setScraps] = useState([]);

  useEffect(() => {
    axios.get(`/dailband/user/${user_id}/scraps`)
      .then(response => {
        setScraps(response.data);
      })
      .catch(error => {
        console.error('There was an error fetching the scraps!', error);
      });
  }, [user_id]);

  const handleDelete = (postId) => {
    axios.delete(`/dailband/user/${user_id}/scraps/${postId}`)
      .then(() => {
        setScraps(scraps.filter(scrap => scrap.post_id !== postId));
      })
      .catch(error => {
        console.error('There was an error deleting the scrap!', error);
      });
  };

  return (
    <div>
      <Header />
      <div className="ScrapPage">
        <div className="Sidebar">
          <div className="UserProfile">
            <img src="/path/to/profile/picture" alt="profile" />
            <div className="Username">윤영선</div>
          </div>
          <div className="Nav">
            <a href="/profile/edit">프로필 수정</a>
            <a href="/scraps" className="active">스크랩</a>
            <a href="/posts">내가 쓴 글</a>
            <a href="/reservations">공연 예약</a>
          </div>
        </div>
        <div className="Content">
          <h2>스크랩</h2>
          <div className="ScrapsList">
            {scraps.map(scrap => (
              <div key={scrap.post_id} className="ScrapItem">
                <h3>{scrap.title}</h3>
                <p>{scrap.content}</p>
                <p>작성자: {scrap.nickname}</p>
                <p>작성 날짜: {new Date(scrap.created_at).toLocaleDateString()}</p>
                <button onClick={() => handleDelete(scrap.post_id)}>삭제</button>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Scrap;
