import { useState } from "react";
import Header from "../components/Header";
import "./Home.css";

function Home() {
  const handleIconClick = (session) => {
    window.location.href = `/${session}`;
  };

  const [showRecruitment, setShowRecruitment] = useState(false);
  const [showIntroduction, setShowIntroduction] = useState(false);
  const [selectedPost, setSelectedPost] = useState(null); // Add selectedPost state here

  const handleRecruitmentClick = () => {
    setShowRecruitment(!showRecruitment);
    setShowIntroduction(false);
  };

  const handleIntroductionClick = () => {
    setShowIntroduction(!showIntroduction);
    setShowRecruitment(false);
  };

  const handlePostClick = (postId) => {
    setSelectedPost(postId);
  };

  const posts = [
    {
      id: 1,
      title: '개인 소개글 내용 1',
      details: '이것은 첫 번째 개인 소개글의 자세한 내용입니다.',
    },
    {
      id: 2,
      title: '개인 소개글 내용 2',
      details: '이것은 두 번째 개인 소개글의 자세한 내용입니다.',
    },
  ];

  return (
    <div>
      <Header />
      <section className="banner">
        <div className="banner-image">
          <img alt="Band" src="img/image 1.png" />
        </div>
        <div className="banner-text">
          <div>밴드 동아리 커뮤니티와</div>
          <div>보컬 음성 분석 서비스를 한번에~!</div>
        </div>
      </section>

      <section className="features">
        <h2>세션끼리 소통하고 싶다면?</h2>
        <div className="icons">
          <div className="icon" onClick={() => handleIconClick('drum')}>
            <span role="img" aria-label="드럼">🥁</span> 드럼
          </div>
          <div className="icon" onClick={() => handleIconClick('guitar')}>
            <span role="img" aria-label="기타">🎸</span> 기타
          </div>
          <div className="icon" onClick={() => handleIconClick('vocal')}>
            <span role="img" aria-label="보컬">🎤</span> 보컬
          </div>
          <div className="icon" onClick={() => handleIconClick('bass')}>
            <span role="img" aria-label="베이스">🎸</span> 베이스
          </div>
          <div className="icon" onClick={() => handleIconClick('keyboard')}>
            <span role="img" aria-label="키보드">🎹</span> 키보드
          </div>
        </div>
      </section>

      <section className="home">
        <h2>동아리 멤버 모집은 여기서!</h2>
        <button onClick={handleRecruitmentClick}>최근 동아리 모집 게시글 보기</button>
        {showRecruitment && (
          <div className="post-list">
            {posts.map((post) => (
              <div key={post.id} onClick={() => handlePostClick(post.id)} className="post-item">
                {post.title}
              </div>
            ))}
          </div>
        )}
        {selectedPost && (
          <div className="post-details">
            {posts.find((post) => post.id === selectedPost).details}
          </div>
        )}
      </section>

      <section className="concerts">
        <h2>연합 공연 관련 소식</h2>
        <div className="concert-cards">
          <div className="concert-card">Concert 1</div>
          <div className="concert-card">Concert 2</div>
          <div className="concert-card">Concert 3</div>
        </div>
      </section>

      <section className="analysis">
        <h2>음역대에 맞는 밴드 음악을 추천해드려요!</h2>
        <p>보컬의 음역대를 분석하여 음악대에 맞는 노래 추천 서비스 제공</p>
        <button>분석하러 가기</button>
      </section>

      <footer className="footer">
        <div className="social-links">
          <a href="#">Instagram</a>
          <a href="#">Facebook</a>
          <a href="#">Twitter</a>
        </div>
      </footer>
    </div>
  );
}

export default Home;
