// src/routes/MyReservations.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './MyReservations.css';

function MyReservations() {
  const [reservations, setReservations] = useState([]);
  const userId = sessionStorage.getItem('user_id'); // 세션에서 user_id 가져오기

  useEffect(() => {
    if (userId) {
      axios.get(`/dailband/user/${userId}/reservations`)
        .then(response => {
          setReservations(response.data);
        })
        .catch(error => {
          console.error('Error fetching reservations:', error);
        });
    }
  }, [userId]);

  const handleCancel = (reservationId) => {
    axios.delete(`/dailband/reservation/${reservationId}`)
      .then(response => {
        setReservations(reservations.filter(reservation => reservation.reservation_id !== reservationId));
      })
      .catch(error => {
        console.error('Error cancelling reservation:', error);
      });
  };

  return (
    <div className="MyReservationsPage">
      <h2>예약한 공연 정보</h2>
      <div className="ReservationsList">
        {reservations.map(reservation => (
          <div className="ReservationCard" key={reservation.reservation_id}>
            <div className="ReservationDetails">
              <div className="ReservationTitle">{reservation.title}</div>
              <div className="ReservationDate">{new Date(reservation.date).toLocaleDateString()} ({reservation.weekday})</div>
              <div className="ReservationVenue">{reservation.venue}</div>
              <div className="ReservationCount">예약 매수 {reservation.count}매</div>
              <button className="CancelButton" onClick={() => handleCancel(reservation.reservation_id)}>예약 취소</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default MyReservations;
