import React, { useState, useEffect } from "react";
import axios from "axios";
import Header from "../components/Header";
import "./Scrap.css";
import { Link } from "react-router-dom";

function Scrap() {
  const [scraps, setScraps] = useState([]);
  const userId = 1; // 실제 사용자 ID로 변경

  useEffect(() => {
    const fetchScraps = async () => {
      try {
        const response = await axios.get(`/dailband/user/${userId}/scraps`);
        setScraps(response.data);
      } catch (error) {
        console.error("Failed to fetch scraps:", error);
      }
    };

    fetchScraps();
  }, [userId]);

  return (
    <div className="ScrapsPage">
      <Header />
      <div className="Sidebar">
        <h2>윤영선</h2>
        <Link to="/ProfileEdit">프로필 수정</Link>
        <a href="/scraps">스크랩</a>
        <a href="#">내가 쓴 글</a>
        <a href="#">공연 예약</a>
      </div>
      <div className="Content">
        <h2>스크랩</h2>
        <div className="ScrapsList">
          {scraps.map((scrap) => (
            <div key={scrap.post_id} className="ScrapItem">
              <h3>{scrap.title}</h3>
              <p><strong>작성자:</strong> {scrap.nickname}</p>
              <p>{scrap.content}</p>
              <p><small>{new Date(scrap.created_at).toLocaleString()}</small></p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Scrap;
