import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link, useParams } from 'react-router-dom';
import Header from '../components/Header';
import './Scrap.css';

function Scrap() {
  const { user_id } = useParams();
  const [scrapPosts, setScrapPosts] = useState([]);
  const [scrapPerformances, setScrapPerformances] = useState([]);

  useEffect(() => {
    const fetchScrapPosts = async () => {
      try {
        const response = await axios.get(`/dailband/user/${user_id}/scraps/posts`);
        console.log('Scrap posts response:', response.data);
        setScrapPosts(response.data.scrap_post);
      } catch (error) {
        console.error('Failed to fetch scrap posts:', error);
      }
    };

    const fetchScrapPerformances = async () => {
      try {
        const response = await axios.get(`/dailband/user/${user_id}/scraps/myperformances`);
        console.log('Scrap performances response:', response.data);
        setScrapPerformances(response.data.scrap_performance);
      } catch (error) {
        console.error('Failed to fetch scrap performances:', error);
      }
    };

    fetchScrapPosts();
    fetchScrapPerformances();
  }, [user_id]);

  const handleDeleteScrapPost = async (postId) => {
    try {
      await axios.delete(`/dailband/user/${user_id}/scraps/posts/${postId}`);
      setScrapPosts(scrapPosts.filter(scrap => scrap.post.post_id !== postId));
    } catch (error) {
      console.error('Failed to delete scrap post:', error);
    }
  };

  const handleDeleteScrapPerformance = async (performanceId) => {
    try {
      await axios.delete(`/dailband/user/${user_id}/scraps/myperformances/${performanceId}`);
      setScrapPerformances(scrapPerformances.filter(scrap => scrap.performance.performance_id !== performanceId));
    } catch (error) {
      console.error('Failed to delete scrap performance:', error);
    }
  };

  return (
    <div className="Scrap">
      <Header />
      <div className="Scrap-container">
        <div className="Sidebar">
          <div className="Profile-picture">
            <img src="/path/to/default-profile.png" alt="프로필 사진" />
          </div>
          <h2>사용자 닉네임</h2>
          <Link to={`/profile/edit/${user_id}`}>프로필 수정</Link>
          <Link to={`/scrap/${user_id}`} className="active">스크랩</Link>
          <Link to={`/myposts/${user_id}`}>내가 쓴 글</Link>
          <Link to={`/myperformances/${user_id}`}>내가 작성한 공연 조회</Link>
          <Link to={`/reservations/${user_id}`}>공연 예약</Link>
        </div>
        <div className="Content">
          <h2>스크랩</h2>
          <h3>스크랩한 게시글</h3>
          {scrapPosts.length === 0 ? (
            <p>스크랩한 게시글이 없습니다.</p>
          ) : (
            <ul>
              {scrapPosts.map((scrap) => (
                <li key={scrap.post.post_id}>
                  <Link to={`/post/${scrap.post.post_id}`}>{scrap.post.title}</Link>
                  <button onClick={() => handleDeleteScrapPost(scrap.post.post_id)}>삭제</button>
                </li>
              ))}
            </ul>
          )}
          <h3>스크랩한 공연</h3>
          {scrapPerformances.length === 0 ? (
            <p>스크랩한 공연이 없습니다.</p>
          ) : (
            <ul>
              {scrapPerformances.map((scrap) => (
                <li key={scrap.performance.performance_id}>
                  <Link to={`/performance/${scrap.performance.performance_id}`}>{scrap.performance.title}</Link>
                  <button onClick={() => handleDeleteScrapPerformance(scrap.performance.performance_id)}>삭제</button>
                </li>
              ))}
            </ul>
          )}
        </div>
      </div>
    </div>
  );
}

export default Scrap;
