/*
import React, { useState, useEffect } from "react";
import axios from "axios";
import Header from "../components/Header";
import "./ProfileEdit.css";
import { Link, useParams } from "react-router-dom";

function ProfileEdit() {
  const { user_id } = useParams();
  const [formData, setFormData] = useState({
    nickname: "",
    email: "",
    password: "",
    passwordConfirm: "",
    sessions: [],
  });

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(`/dailband/user/${user_id}/profile`);
        setFormData(response.data);
      } catch (error) {
        console.error(error);
      }
    };

    fetchData();
  }, [user_id]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleCheckboxChange = (e) => {
    const { value, checked } = e.target;
    setFormData((prevData) => {
      if (checked) {
        return { ...prevData, sessions: [...prevData.sessions, value] };
      } else {
        return {
          ...prevData,
          sessions: prevData.sessions.filter((session) => session !== value),
        };
      }
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (formData.password !== formData.passwordConfirm) {
      alert("비밀번호가 일치하지 않습니다.");
      return;
    }
    try {
      await axios.put(`/dailband/user/${user_id}/profile`, formData);
      alert("프로필이 성공적으로 업데이트되었습니다.");
    } catch (error) {
      console.error(error);
      alert("프로필 업데이트 중 오류가 발생했습니다.");
    }
  };

  return (
    <div className="ProfileEdit">
      <Header />
      <div className="ProfileEdit-container">
        <div className="Sidebar">
          <div className="Profile-picture">
            <img src="/path/to/default-profile.png" alt="프로필 사진" />
          </div>
          <h2>{formData.nickname}</h2>
          <Link to={`/profile/edit/${user_id}`} className="active">프로필 수정</Link>
          <Link to="/scrap">스크랩</Link>
          <Link to="/MyPost">내가 쓴 글</Link>
          <Link to="/MyReservations">공연 예약</Link>
        </div>
        <div className="Content">
          <h2>마이페이지</h2>
          <form onSubmit={handleSubmit}>
            <div className="InputField">
              <label>닉네임</label>
              <input
                type="text"
                name="nickname"
                value={formData.nickname}
                onChange={handleChange}
              />
            </div>
            <div className="InputField">
              <label>이메일</label>
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
              />
            </div>
            <div className="InputField">
              <label>비밀번호 변경</label>
              <input
                type="password"
                name="password"
                value={formData.password}
                onChange={handleChange}
              />
            </div>
            <div className="InputField">
              <label>비밀번호 확인</label>
              <input
                type="password"
                name="passwordConfirm"
                value={formData.passwordConfirm}
                onChange={handleChange}
              />
            </div>
            <div className="CheckboxGroup">
              <label>세션 정보</label>
              {["드럼", "기타", "보컬", "베이스", "키보드"].map((role) => (
                <label key={role}>
                  <input
                    type="checkbox"
                    name="sessions"
                    value={role}
                    checked={formData.sessions.includes(role)}
                    onChange={handleCheckboxChange}
                  />
                  {role}
                </label>
              ))}
            </div>
            <button type="submit" className="UpdateButton">
              수정
            </button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default ProfileEdit;



