import React, { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import Header from "../components/Header";
import "./ProfileEdit.css";

function ProfileEdit() {
  const navigate = useNavigate();
  const { user_id } = useParams();
  const [profile, setProfile] = useState(null);
  const [formData, setFormData] = useState({
    name: "",
    username: "",
    email: "",
    password: "",
    confirmPassword: "",
    roles: [], // Updated to store an array of roles
  });

  const roles = ["드럼", "기타", "베이스", "보컬", "키보드"]; // List of roles

  useEffect(() => {
    // Fetch user profile from the API
    fetch(`/dailband/user/${user_id}/profile`)
      .then((response) => response.json())
      .then((data) => {
        setProfile(data);
        setFormData({
          name: data.name,
          username: data.username,
          email: data.email,
          password: "",
          confirmPassword: "",
          roles: data.roles || [], // Assuming the API returns an array of roles
        });
      })
      .catch((error) => console.error("Error fetching profile:", error));
  }, [user_id]);

  function handleChange(e) {
    const { name, value } = e.target;
    setFormData((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  }

  function handleRoleChange(e) {
    const { value, checked } = e.target;
    setFormData((prevState) => {
      const newRoles = checked
        ? [...prevState.roles, value]
        : prevState.roles.filter((role) => role !== value);

      return {
        ...prevState,
        roles: newRoles,
      };
    });
  }

  function handleUpdateProfile() {
    // Update user profile via the API
    fetch(`/dailband/user/${user_id}/profile`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(formData),
    })
      .then((response) => response.json())
      .then((data) => {
        setProfile(data);
        alert("Profile updated successfully!");
        // Navigate to the profile detail page after successful update
        navigate(`/profile/${user_id}`);
      })
      .catch((error) => console.error("Error updating profile:", error));
  }

  return (
    <div>
      <Header />
      <div className="ProfileEditPage">
        <div className="TitleEdit">마이페이지</div>
        <div className="InputEdit">
          <input
            placeholder="닉네임"
            name="name"
            value={formData.name}
            onChange={handleChange}
          />
          <input
            placeholder="이메일"
            name="email"
            value={formData.email}
            onChange={handleChange}
          />
          <input
            type="password"
            placeholder="비밀번호 변경"
            name="password"
            value={formData.password}
            onChange={handleChange}
          />
          <input
            type="password"
            placeholder="비밀번호 확인"
            name="confirmPassword"
            value={formData.confirmPassword}
            onChange={handleChange}
          />
          <div className="CheckboxGroup">
            {roles.map((role) => (
              <label key={role}>
                <input
                  type="checkbox"
                  value={role}
                  checked={formData.roles.includes(role)}
                  onChange={handleRoleChange}
                />
                {role}
              </label>
            ))}
          </div>
        </div>
        <div className="SubmitBtns">
          <button className="EditSubmit" onClick={handleUpdateProfile}>
            수정 완료
          </button>
        </div>
      </div>
      <div className="Background">
        <img alt="background" src="img/배경사진.jpg" />
      </div>
    </div>
  );
}

export default ProfileEdit;
