import React, { useState, useEffect } from 'react';
import { Link, useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
import Header from '../components/Header';
import './Scrap.css';

function Scrap() {
  const { user_id } = useParams();
  const navigate = useNavigate();
  const [scraps, setScraps] = useState([]);

  useEffect(() => {
    const fetchScraps = async () => {
      try {
        const response = await axios.get(`/dailband/user/${user_id}/scraps`);
        setScraps(response.data);
      } catch (error) {
        console.error('Failed to fetch scraps:', error);
      }
    };

    fetchScraps();
  }, [user_id]);

  return (
    <div className="Scrap">
      <Header />
      <div className="Scrap-container">
        <div className="Sidebar">
          <div className="Profile-picture">
            <img src="/path/to/default-profile.png" alt="프로필 사진" />
          </div>
          <h2>윤영선</h2>
          <Link to={`/profile/edit/${user_id}`}>프로필 수정</Link>
          <Link to={`/scrap/${user_id}`} className="active">스크랩</Link>
          <Link to={`/myposts/${user_id}`}>내가 쓴 글</Link>
          <Link to={`/reservations/${user_id}`}>공연 예약</Link>
        </div>
        <div className="Content">
          <h2>스크랩</h2>
          <div className="Scrap-list">
            {scraps.map((scrap) => (
              <div key={scrap.id} className="Scrap-item">
                <img src={scrap.image} alt={scrap.title} />
                <div className="Scrap-info">
                  <h3>{scrap.title}</h3>
                  <p>{scrap.description}</p>
                  <button onClick={() => console.log('삭제 기능 구현 필요')}>삭제</button>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Scrap;
