import React, { useState, useEffect } from 'react';
import { Link, useParams } from 'react-router-dom';
import axios from 'axios';
import Header from '../components/Header';
import './Scrap.css';

function Scrap() {
  const { user_id } = useParams();
  const [scrapPosts, setScrapPosts] = useState([]);
  const [scrapPerformances, setScrapPerformances] = useState([]);

  useEffect(() => {
    const fetchScrapPosts = async () => {
      try {
        const response = await axios.get(`/dailband/user/${user_id}/scraps/posts`);
        setScrapPosts(response.data.scrap_post);
      } catch (error) {
        console.error('Failed to fetch scrap posts:', error);
      }
    };

    const fetchScrapPerformances = async () => {
      try {
        const response = await axios.get(`/dailband/user/${user_id}/scraps/myperformances`);
        setScrapPerformances(response.data.scrap_performance);
      } catch (error) {
        console.error('Failed to fetch scrap performances:', error);
      }
    };

    fetchScrapPosts();
    fetchScrapPerformances();
  }, [user_id]);

  const handleDeletePost = async (postId) => {
    try {
      await axios.delete(`/dailband/posts/${postId}`);
      setScrapPosts(scrapPosts.filter(({ post }) => post.post_id !== postId));
    } catch (error) {
      console.error('Error deleting post:', error);
    }
  };

  const handleDeletePerformance = async (performanceId) => {
    try {
      await axios.delete(`/dailband/performances/${performanceId}`);
      setScrapPerformances(scrapPerformances.filter(({ performance }) => performance.performance_id !== performanceId));
    } catch (error) {
      console.error('Error deleting performance:', error);
    }
  };

  return (
    <div className="Scrap">
      <Header />
      <div className="Scrap-container">
        <div className="Sidebar">
          <div className="Profile-picture">
            <img src="/path/to/default-profile.png" alt="프로필 사진" />
          </div>
          <h2>윤영선</h2>
          <Link to={`/profile/edit/${user_id}`}>프로필 수정</Link>
          <Link to={`/scrap/${user_id}`} className="active">스크랩</Link>
          <Link to={`/myposts/${user_id}`}>내가 쓴 글</Link>
          <Link to={`/reservations/${user_id}`}>공연 예약</Link>
        </div>
        <div className="Content">
          <h2>공연 홍보 게시글</h2>
          <div className="ScrapPerformances">
            {scrapPerformances.length > 0 ? (
              scrapPerformances.map(({ performance }) => (
                <div key={performance.performance_id} className="PerformanceCard">
                  <img src={performance.image_path} alt={performance.title} className="PerformanceImage" />
                  <div className="PerformanceInfo">
                    <h3>{performance.title}</h3>
                    <p>{performance.content}</p>
                    <small>{performance.date} {performance.time}</small>
                    <p>{performance.venue}</p>
                    <button onClick={() => handleDeletePerformance(performance.performance_id)}>삭제</button>
                  </div>
                </div>
              ))
            ) : (
              <p>스크랩한 공연이 없습니다.</p>
            )}
          </div>

          <h2>게시글</h2>
          <div className="ScrapPosts">
            {scrapPosts.length > 0 ? (
              scrapPosts.map(({ post }) => (
                <div key={post.post_id} className="PostCard">
                  <div className="PostInfo">
                    <h3>{post.title}</h3>
                    <p>{post.content}</p>
                    <small>작성날짜: {new Date(post.created_at).toLocaleDateString()}</small>
                    <button onClick={() => handleDeletePost(post.post_id)}>삭제</button>
                  </div>
                </div>
              ))
            ) : (
              <p>스크랩한 게시글이 없습니다.</p>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Scrap;
