import React, { useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import Header from '../components/Header';
import './ProfileEdit.css';

function ProfileEdit() {
  const navigate = useNavigate();
  const { user_id } = useParams();
  const [profile, setProfile] = useState(null);
  const [formData, setFormData] = useState({
    name: '',
    username: '',
    email: '',
    role: '',
    description: ''
  });

  useEffect(() => {
    // Fetch user profile from the API
    fetch(`/dailband/user/${user_id}/profile`)
      .then(response => response.json())
      .then(data => {
        setProfile(data);
        setFormData({
          name: data.name,
          username: data.username,
          email: data.email,
          role: data.role,
          description: data.description
        });
      })
      .catch(error => console.error('Error fetching profile:', error));
  }, [user_id]);

  function handleChange(e) {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
  }

  function handleUpdateProfile() {
    // Update user profile via the API
    fetch(`/dailband/user/${user_id}/profile`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(formData)
    })
      .then(response => response.json())
      .then(data => {
        setProfile(data);
        alert('Profile updated successfully!');
        // Navigate to the profile detail page after successful update
        navigate(`/profile/${user_id}`);
      })
      .catch(error => console.error('Error updating profile:', error));
  }

  return (
    <div>
      <Header />
      <div className="ProfileEditPage">
        <div className="TitleEdit">프로필 수정</div>
        <div className="InputEdit">
          <input
            placeholder="이름"
            name="name"
            value={formData.name}
            onChange={handleChange}
          />
          <input
            placeholder="유저네임"
            name="username"
            value={formData.username}
            onChange={handleChange}
          />
          <input
            placeholder="이메일"
            name="email"
            value={formData.email}
            onChange={handleChange}
          />
          <input
            placeholder="역할"
            name="role"
            value={formData.role}
            onChange={handleChange}
          />
          <textarea
            placeholder="설명"
            name="description"
            value={formData.description}
            onChange={handleChange}
          ></textarea>
        </div>
        <div className="SubmitBtns">
          <button className="EditSubmit" onClick={handleUpdateProfile}>
            수정 완료
          </button>
        </div>
      </div>
      <div className="Background">
        <img alt="background" src="img/배경사진.jpg" />
      </div>
    </div>
  );
}

export default ProfileEdit;
