import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import Header from '../components/Header';
import Sidebar from '../components/Sidebar';
import './ProfileEdit.css';

function ProfileEdit() {
  const navigate = useNavigate();
  const [userId, setUserId] = useState(null);
  const [formData, setFormData] = useState({
    nickname: '',
    email: '',
    password: '',
    passwordConfirm: '',
    sessions: [],
  });

  useEffect(() => {
    const fetchUserId = async () => {
      try {
        const response = await axios.get('/dailband/user/me');
        setUserId(response.data.user_id);
      } catch (error) {
        console.error('Failed to fetch user ID:', error);
        navigate('/login');
      }
    };

    fetchUserId();
  }, [navigate]);

  useEffect(() => {
    if (userId) {
      const fetchData = async () => {
        try {
          const response = await axios.get(`/dailband/user/${userId}/profile`);
          setFormData(response.data);
        } catch (error) {
          console.error(error);
        }
      };

      fetchData();
    }
  }, [userId]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (formData.password !== formData.passwordConfirm) {
      alert('비밀번호가 일치하지 않습니다.');
      return;
    }
    try {
      await axios.put(`/dailband/user/${userId}/profile`, formData);
      alert('프로필이 성공적으로 업데이트되었습니다.');
    } catch (error) {
      console.error(error);
      alert('프로필 업데이트 중 오류가 발생했습니다.');
    }
  };

  if (!userId) {
    return <p>Loading...</p>;
  }

  return (
    <div className="ProfileEdit">
      <Header />
      <div className="ProfileEdit-container">
        <Sidebar userId={userId} nickname={formData.nickname} />
        <div className="Content">
          <h2>마이페이지</h2>
          <form onSubmit={handleSubmit}>
            <div className="InputField">
              <label>닉네임</label>
              <input
                type="text"
                name="nickname"
                value={formData.nickname}
                onChange={handleChange}
              />
            </div>
            <div className="InputField">
              <label>이메일</label>
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
              />
            </div>
            <div className="InputField">
              <label>비밀번호 변경</label>
              <input
                type="password"
                name="password"
                value={formData.password}
                onChange={handleChange}
              />
            </div>
            <div className="InputField">
              <label>비밀번호 확인</label>
              <input
                type="password"
                name="passwordConfirm"
                value={formData.passwordConfirm}
                onChange={handleChange}
              />
            </div>
            <button type="submit">저장</button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default ProfileEdit;
