import { useState } from "react";
import Header from "../components/Header";
import "./Home.css";

function Home() {
  const handleIconClick = (session) => {
    // ÏÑ∏ÏÖò ÌéòÏù¥ÏßÄÎ°ú Ïù¥ÎèôÌïòÎäî Ìï®Ïàò (URLÏùÄ ÌïÑÏöîÏóê ÎßûÍ≤å ÏàòÏ†ï)
    window.location.href = `/${session}`;
  };

  const [showRecruitment, setShowRecruitment] = useState(false);
  const [selectedPost, setSelectedPost] = useState(null);

  const handleRecruitmentClick = () => {
    setShowRecruitment(!showRecruitment);
    setSelectedPost(null); // Í∏∞Ï°¥Ïóê ÏÑ†ÌÉùÎêú Í≤åÏãúÍ∏Ä Ï¥àÍ∏∞Ìôî
  };

  const handlePostClick = (postId) => {
    setSelectedPost(postId);
  };

  const posts = [
    {
      id: 1,
      title: 'Í∞úÏù∏ ÏÜåÍ∞úÍ∏Ä ÎÇ¥Ïö© 1',
      details: 'Ïù¥Í≤ÉÏùÄ Ï≤´ Î≤àÏß∏ Í∞úÏù∏ ÏÜåÍ∞úÍ∏ÄÏùò ÏûêÏÑ∏Ìïú ÎÇ¥Ïö©ÏûÖÎãàÎã§.',
    },
    {
      id: 2,
      title: 'Í∞úÏù∏ ÏÜåÍ∞úÍ∏Ä ÎÇ¥Ïö© 2',
      details: 'Ïù¥Í≤ÉÏùÄ Îëê Î≤àÏß∏ Í∞úÏù∏ ÏÜåÍ∞úÍ∏ÄÏùò ÏûêÏÑ∏Ìïú ÎÇ¥Ïö©ÏûÖÎãàÎã§.',
    },
  ];

  return (
    <div>
      <Header />
      <section className="banner">
        <div className="banner-image">
          <img alt="Band" src="img/image 1.png" />
        </div>
        <div className="banner-text">
          <div>Î∞¥Îìú ÎèôÏïÑÎ¶¨ Ïª§ÎÆ§ÎãàÌã∞ÏôÄ</div>
          <div>Î≥¥Ïª¨ ÏùåÏÑ± Î∂ÑÏÑù ÏÑúÎπÑÏä§Î•º ÌïúÎ≤àÏóê~!</div>
        </div>
      </section>

      <section className="features">
        <h2>ÏÑ∏ÏÖòÎÅºÎ¶¨ ÏÜåÌÜµÌïòÍ≥† Ïã∂Îã§Î©¥?</h2>
        <div className="icons">
          <div className="icon" onClick={() => handleIconClick('drum')}>
            <span role="img" aria-label="ÎìúÎüº">ü•Å</span> 
            <div className="icon-label">ÎìúÎüº</div>
          </div>
          <div className="icon" onClick={() => handleIconClick('guitar')}>
            <span role="img" aria-label="Í∏∞ÌÉÄ">üé∏</span> 
            <div className="icon-label">Í∏∞ÌÉÄ</div>
          </div>
          <div className="icon" onClick={() => handleIconClick('vocal')}>
            <span role="img" aria-label="Î≥¥Ïª¨">üé§</span> 
            <div className="icon-label">Î≥¥Ïª¨</div>
          </div>
          <div className="icon" onClick={() => handleIconClick('bass')}>
            <span role="img" aria-label="Î≤†Ïù¥Ïä§">üé∏</span> 
            <div className="icon-label">Î≤†Ïù¥Ïä§</div>
          </div>
          <div className="icon" onClick={() => handleIconClick('keyboard')}>
            <span role="img" aria-label="ÌÇ§Î≥¥Îìú">üéπ</span> 
            <div className="icon-label">ÌÇ§Î≥¥Îìú</div>
          </div>
        </div>
      </section>

      <section className="home">
      <h2>ÎèôÏïÑÎ¶¨ Î©§Î≤Ñ Î™®ÏßëÏùÄ Ïó¨Í∏∞ÏÑú!</h2>
      <div className="buttons">
        <button onClick={() => handleButtonClick('recruitment')} className={showRecruitment ? 'active' : ''}>
          ÏµúÍ∑º ÎèôÏïÑÎ¶¨ Î™®ÏßëÍ∏Ä Î≥¥Í∏∞
        </button>
        <button onClick={() => handleButtonClick('introduction')} className={!showRecruitment ? 'active' : ''}>
          ÏµúÍ∑º Í∞úÏù∏ ÏÜåÍ∞úÍ∏Ä Î≥¥Í∏∞
        </button>
      </div>
      {showRecruitment && (
        <div className="post-list">
          {posts.map((post) => post.type === 'recruitment' && (
            <div key={post.id} onClick={() => handlePostClick(post.id)} className="post-item">
              {post.title}
            </div>
          ))}
        </div>
      )}
      {!showRecruitment && (
        <div className="post-list">
          {posts.map((post) => post.type === 'introduction' && (
            <div key={post.id} onClick={() => handlePostClick(post.id)} className="post-item">
              {post.title}
            </div>
          ))}
        </div>
      )}
      {selectedPost && (
        <div className="post-details">
          {posts.find((post) => post.id === selectedPost).details}
        </div>
      )}
    </section>

      <section className="concerts">
        <h2>Ïó∞Ìï© Í≥µÏó∞ Í¥ÄÎ†® ÏÜåÏãù</h2>
        <div className="concert-cards">
          <div className="concert-card">Concert 1</div>
          <div className="concert-card">Concert 2</div>
          <div className="concert-card">Concert 3</div>
        </div>
      </section>

      <section className="analysis">
        <h2>ÏùåÏó≠ÎåÄÏóê ÎßûÎäî Î∞¥Îìú ÏùåÏïÖÏùÑ Ï∂îÏ≤úÌï¥ÎìúÎ†§Ïöî!</h2>
        <p>Î≥¥Ïª¨Ïùò ÏùåÏó≠ÎåÄÎ•º Î∂ÑÏÑùÌïòÏó¨ ÏùåÏïÖÎåÄÏóê ÎßûÎäî ÎÖ∏Îûò Ï∂îÏ≤ú ÏÑúÎπÑÏä§ Ï†úÍ≥µ</p>
        <button>Î∂ÑÏÑùÌïòÎü¨ Í∞ÄÍ∏∞</button>
      </section>

      <footer className="footer">
        <div className="social-links">
          <a href="#">Instagram</a>
          <a href="#">Facebook</a>
          <a href="#">Twitter</a>
        </div>
      </footer>
    </div>
  );
}

export default Home;
