              <ul className="ScrapPosts">
                {scrapPosts.map((scrap) => (
                  <li key={scrap.post.post_id} className="ScrapItem">
                    <div>
                      <Link to={`/post/${scrap.post.post_id}`}>
                        <h4>{scrap.post.title}</h4>
                        <p>{scrap.post.content}</p>
                      </Link>
                      <p>작성날짜: {new Date(scrap.post.created_at).toLocaleDateString()}</p>
                    </div>
                    <button onClick={() => handleDeleteScrapPost(scrap.post.post_id)}>삭제</button>
